// Expected input payload example:
// { slot: 3, type: "Ultrasonic", data_type: "Input", data: 56 }

let msgOut = null;

// Ensure payload exists
if (typeof msg.payload === "object" && msg.payload.type) {
    const type = msg.payload.type.toLowerCase();  // normalize text
    const slot = msg.payload.slot || 0;

    // === Separate outputs based on sensor type ===
    switch (type) {
        case "ir_distance":
            // Example: differentiate multiple IR sensors by slot
            if (slot === 1) {
                msg.topic = "IR_Sensor_1";
                msgOut = [msg, null, null, null];
            } else if (slot === 2) {
                msg.topic = "IR_Sensor_2";
                msgOut = [null, msg, null, null];
            }
            break;

        case "ultrasonic":
            msg.topic = "Ultrasonic_Trigger";
            msgOut = [null, null, msg, null];
            break;

        case "color_sensor":
            msg.topic = "Camera_Color";
            msgOut = [null, null, null, msg];
            break;

        default:
            msg.topic = "Unknown";
            msgOut = [null, null, null, null];
            break;
    }
}

// Output order:
// [0] → IR node 1
// [1] → IR node 2
// [2] → Ultrasonic
// [3] → Camera Color
return msgOut;

// Expecting input payload like: { slot: 1, type: "IR_distance", data_type: "Input", data: 0 or 1 }

// Extract IR value
let irValue = msg.payload.data;

// Create command message
let servoMsg = {};

if (irValue == 1) {
    // Object detected
    servoMsg = { "servo": 1, "angle": 90 };
} else {
    // No object
    servoMsg = { "servo": 1, "angle": 0 };
}

// Replace original payload with servo command
msg.payload = servoMsg;

// Optionally change topic for clarity
msg.topic = "servo_control";

return msg;

